"use client";

import React, { useState } from "react";
import {
  Box,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  IconButton,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import Sidebar from "../../components/sideBar";
import { useAppSelector } from "../redux/hooks/reduxHooks";
import StudentCard from "../../components/students/cards"

const studentsData = [
  { id: 1, name: "John Smith", image: "data:image/jpeg;base64," },
  { id: 2, name: "Emily Davis", image: "data:image/jpeg;base64," },
  { id: 3, name: "Michael Johnson", image: "data:image/jpeg;base64," },
  { id: 4, name: "Sophia Brown", image: "data:image/jpeg;base64," },
  { id: 5, name: "William Martinez", image: "data:image/jpeg;base64," },
  { id: 6, name: "Olivia Wilson", image: "data:image/jpeg;base64," },
  { id: 7, name: "James Anderson", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSU5H4jCmhH0BOHclWDTve-sPALU046j182Mg&s" },
  { id: 8, name: "Isabella Thomas", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRFnglEW-VKNz03Ud57iLiuppxoHZw_1wQL_Q&s" },
  { id: 9, name: "Alexander Lee", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTufOS8Jv6vdd8uDZbUoxNb_8SJa6_0MHT4A&s" },
  { id: 10, name: "Mia White", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSXLz1W2eRU1Q51Sye4WPJ5DJ0YFpQQn9t3lQ&s" },
];

const Dashboard = () => {
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const teacherState = useAppSelector((state) => state.teacherReducer);

  const toggleDrawer = () => {
    setIsDrawerOpen(!isDrawerOpen);
  };

  return (
    <Box sx={{ display: "flex", position: "relative", height: "100vh" }}>
      <CssBaseline />
      <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="menu"
            onClick={toggleDrawer}
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap component="div">
            Dashboard
          </Typography>
        </Toolbar>
      </AppBar>

      <Sidebar isOpen={isDrawerOpen} />

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          mt: 8,
          display: "flex",
          flexWrap: "wrap",
          gap: 3,
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        {teacherState.dispalyAllStudents ? (
          studentsData.map((student) => (
            <StudentCard
              key={student.id}
              visible={teacherState.dispalyAllStudents}
              name={student.name}
              image={student.image}
            />
          ))
        ) : (
          <Box>
            <Typography variant="h4" gutterBottom>
              Welcome to the Dashboard
            </Typography>
            <Typography paragraph>
              This is the main content area. Use the sidebar to navigate.
            </Typography>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default Dashboard;
